import React from 'react';
import styles from './Recipes.module.css';
import NavBar from '../../components/Layout/NavBar';
import Heading from '../../components/Layout/Heading';
import recipes from '../../data/recipes'
import { useNavigate } from 'react-router-dom';


function RecipeCard({recipe, onClick}) {
    return (
        <div className={styles.recipe} onClick={onClick}>
            <img src={`${process.env.PUBLIC_URL}/images/foods/${recipe.imgName}`} />
            <span>{recipe.name.toUpperCase()}</span>
        </div>
    )
}

function Recipes() {
  const navigate = useNavigate()
  return (
    <div className={styles.background}>
      <NavBar/>
      <Heading text={'RECIPE LIST'}/>
      <div className={styles.recipes}>
        {recipes.map((recipe)=> (
            <RecipeCard recipe={recipe} key={recipe.name} onClick={()=> {navigate('recipes/recipe')}}/>
        ))}
      </div>
    </div>

  );
}

export default Recipes;
