import React from 'react';
import styles from '../../styles/common.module.css'
import { Link, useLocation } from 'react-router-dom';
import { IoChevronBack } from 'react-icons/io5';

function NavLink({ to, name }) {
    const location = useLocation();
    return (
      <Link to={to} >
        <span style={location.pathname === to ? {fontWeight: 600} : null}>
            {name}
        </span>
      </Link>
    );
  }


function NavBar() {

  return (
    <div className={styles.navBar}>
    <Link to='/'className={styles.back}>
        <IoChevronBack size={40} style={{color: 'white', paddingLeft: '20px'}}/>
        <span style={{fontWeight: 600}}>Back</span>
    </Link>

    <div className={styles.nav}>
        <NavLink to='/' name={'Home'}/>
        <NavLink to='/Recipes' name={'Recipes'}/>
        <NavLink to='/Restaurants' name={'Restaurants'}/>
    </div>
    </div>

  );
}

export default NavBar;
