import React, { useState } from 'react';
import styles from './Recipes.module.css';
import NavBar from '../../components/Layout/NavBar';
import Heading from '../../components/Layout/Heading';
import recipes from '../../data/recipes'

function RecipeCard({recipe, setShowRecipe}) {
    return (
        <div className={styles.recipe} onClick={()=> setShowRecipe(recipe)}>
            <img src={`${process.env.PUBLIC_URL}/images/foods/${recipe.imgName}`} />
            <span>{recipe.name.toUpperCase()}</span>
        </div>
    )
}

function RecipeDetails({recipe}) {
  return (
    <></>
  )
}


function Recipes() {
  const [showRecipe, setShowRecipe] = useState(null)
  const onBack = showRecipe ? () => setShowRecipe(null) : null
  return (
    <div className={styles.background}>
       <NavBar onBack={onBack}/>
       {!showRecipe && <Heading text={'RECIPE LIST'}/>}
       {!!!showRecipe ? 
       <div className={styles.recipes}>
            {recipes.map((recipe)=> (
                <RecipeCard recipe={recipe} setShowRecipe={setShowRecipe} key={recipe.name}/>
            ))}
       </div>
      : <RecipeDetails recipe={showRecipe}/>
      }

    </div>

  );
}

export default Recipes;
