import React from 'react';
import styles from './Recipes.module.css';
import { useParams } from 'react-router-dom';
import recipes from '../../data/recipes'

function Title({title, cookTime}) {
    return (
        <div className={styles.recipeTitle}>
            <div className={styles.recipeSubtitle}>{cookTime}</div>
            <span>{title}</span>
        </div>
    )
}

function RecipeDetails() {
  const params = useParams();
  const recipe = recipes.find((recipe) => recipe.id === params.recipe)
  return (
    <>
        <Title title={recipe.name.toUpperCase()} cookTime={recipe.cookTime}/>
        <img src={require(`../../assets/images/foods/${recipe.imgName}`)} />
    </>
  )
}

export default RecipeDetails;
