const recipes = [{
  name: 'Garlic Rice',
  imgName: 'garlic-rice.jpg',
  cookTime: '15 mins',
  desc: 'Garlic-y & soy-sauce-y, perfect for those days when plain rice feels just a little too boring.',
  ingredients: [['100g pork belly', '1 shallot', '4 cloves garlic', '1 cup cooked rice'], ['20g butter', '40ml soy sauce', '8g sugar']],
