import React from 'react';
import styles from '../../styles/common.module.css'
import { Link, useLocation } from 'react-router-dom';



function Heading({text}) {
  const heading = {
    backgroundColor: '#272422',
    display: 'inline-flex'
  }
  const headingText = {
    fontWeight: 900,
    fontSize: '36px',
    lineHeight: '54px',
    paddingLeft: '1.8em',
    paddingRight: '1.8em',
    marginTop: '0.4em',
    paddingTop: '0.4em',
    paddingBottom: '0.4em',  
    letterSpacing: '0.28em',
    color: '#ffffff',
  }
  return (
    <div style={heading}>
      <span style={headingText}>{text}</span>
    </div>

  );
}



export default Heading;
